body {
    background-image:    url("https://i.imgur.com/8dfzF2h.gif");
	background-repeat: no-repeat;
    background-attachment: fixed;
    background-position: center;
	background-size: cover;
}

/*Main background settings*/
.panel {
    background-color: rgba(12, 154, 255,0.6);
    border-radius: 10px;
    color: #fff;
}

/*Color change on hover*/
.panel:hover {
    background-color: rgba(12, 154, 255,0.8);
}

/*Searchbox*/
.form-control {
    background-color: rgba(255,255,255,0.4);
    border-radius: 10px;
}

/*Searchbox on hover*/
.form-control:hover {
    background-color: rgba(255,255,255,0.8);
}

/*Button Settings*/
.btn {
    border-radius: 10px;
}

/*Search results heading*/
.panel-heading {
    border-radius: 10px;
    color: #fff !important;
	background-color: #0564a8 !important;    
}

/*Search result boxes*/
.details-panel {
	background-color: #0564a8;   
    border-radius: 5px;
}

/*History link above notes*/
.history-button {
	color: white;    
}

/*More white text*/
.list-group-item {
	color: white !important;
    background-color: #0564a8 !important;
}


.list-group-item-heading, .list-group-item-text {
    color: white !important;
}

/*Dropdown menu and Navbar settings*/

/*Navbar background color*/
.navbar {
   background-color: #0564a8;
}


.dropdown-menu, .dropdown-menu li a {
    background-color: #0564a8 !important;
    color: white !important;
}

/*Dropdown links on hover*/
li .dropdown-toggle:hover, .dropdown-menu li a:hover, li.open .dropdown-toggle {
     background-color: #02497c !important;
}

tr.clickable {
    background-color: #02497c;
}

.clickable:hover td {
    background-color: #02497c;
}

/*Search result color changes*/
.vip {
    background-color: #bf1313 !important;
}

.lrp {
    background-color: #8d9921 !important;
    color: black !important;
}

.lrp .list-group-item-heading, .lrp .list-group-item-text {
    color: black !important;
}

.extension {
    background-color: #00911d !important;
}

.osu-id-match {
    background-color: #5694f7 !important;
}

/*Inside box background color*/
.well {
    background-color: #0564a8 !important;
    border-radius: 5px;
}

/*Inside box text colors*/
.well td, .well th {
        color: white !important;
}

td a, .external-reports a, .col-md-8 a {
    color: #77abff;
}

/*Notes box text color*/
textarea {
    color: black;
}


.panel-footer {
    background-color: rgba(0,0,0,0);
}

/*Hides disclaimer button*/
.btn-disclaimer {
    display: none !important;
}

/*Background*/
.DetailTableContainer, .SubDetailPanel, .HelpPanel {
    background-color: #555 !important;
    color: white;
}

/*Red Bar*/
.MainDetailHeaderPanel, .SubDetailHeaderPanel {
    background-color: #555 !important;
}

/*Search Bar*/
.DetailTableHeaderPanel {
    background-color: #555;
}

/*Command Icons and boat*/
.CommandPanel, .EmptyPanel img, .DetailTabList, .DetailTableHeaderPanel a {
    filter:brightness(300%);
}

/*Active session*/
.Selected td {
    background-color: #777 !important;
}

.Data {
    color: black;
}

/*Main background color*/

body, #divHeader, .comment, .ms-drop, .modal-content, #divStandardFields, .white-bg, #TodosMenu, #divFeedback2, .breadcrumb {
	background-color: #5f636b !important;
}

/*Hide default link*/
.organization-link a {
    visibility: hidden;
}

/*COHO stlye header*/
.organization-link {
    background-image: url("https://secure.oregonstate.edu/u_central/images/blackWordmark.gif");
    background-repeat: no-repeat;
    background-color: black;
    margin-bottom: 2em;
    box-shadow: 0px 10px #e55302;
}
/*Fixed header margin*/
.organization-link {
    margin: 0px;
}

/*white user text*/
i.fa .fa-user {
    color: white !important;
}

/*Applications menu background*/
#ApplicationsMenu {
    background-color: #797e84;
}

/*Desktop module background*/
.desktop-module {
    background-color: #bbb;
}

/*More text at needs to be white*/
#upDetails, .control-label, h1,  h2, label, #ttDescription, .gutter-top-xs, .gray, .absoluteDate, .gutter-left-sm, .lead, .text-muted, .col-sm-6, .disp-group {
    color: white;
}

/*Default color of hyperlinks*/
a {
    color: #5394fc;
    text-decoration: underline;
}


/*Tables*/

.table tr:nth-child(odd), .table tr:nth-child(odd) td, .table tr:nth-child(odd) a {
    background-color: #696f7a !important;
    color: white !important;
}

.table tr:nth-child(even), .table tr:nth-child(even) td, .table tr:nth-child(even) span a {
    background-color: #c9c9c9 !important;
    color: black !important;
}

/*Alternating ticket table color*/
.TDAlternatingRow {
    background-color: #696f7a !important;
}

/*Alternating ticket table text color*/
.TDAlternatingRow  a, .TDAlternatingRow td {
    color: white;
}

/*Page number color*/
tr.TDPagerRow span {
    color: black !important;
}

/*More text to be white*/
#thTicket_lblStatus, .gray, .fieldsContainer, .user-link span {
     color: white !important;
}

/*Text to be black*/
td a, td, .pull-right, .gutter-top-xs, .ModuleContent, .popover, .panel-body div {
    color: black;                                       
}

/*Navbar background color*/
.navbar, .TabButtonCell, .panel-body {
    background-color: #cfd1d3 !important;
    color: black;
}

/*More white text*/
#divToDos, #dropdownMenu1, .block-info, .col-md-12, .frmFldLbl, #ctl00_lblStatus, .status {
    color: white !important;
}

/*More black text*/
.pull-right label, .feed-reply .absoluteDate, .feed-reply  {
    color: black !important;
}


/*Responsible Dropdown text color*/
.lu_tdac-auto {
    color: black;
}


/*Details label*/
.panel label {
    color: black;
}

/*Requestor Black Text*/
#pcRequestor_divPersonInfo .gutter-top-xs {
    color: black !important;
}

/*MyWork sidebar*/
#navPrimary span {
    color: white;
}

.sidebar-wrapper {
    background-color: #535860 !important;
}

.sidebar-wrapper a {
    color: #89b6ff !important;
}

/*End MyWork Sidebar*/

/*IT sidebar*/
#divLeft {
    background-color: #535860 !important;
}

#divLeft .text {
    color: white !important;
}

#divLeft .selected .text, #divLeft .text:hover {
    color: black !important;
}

.sidebar-wrapper a {
    color: #89b6ff !important;
}

/*End IT Sidebar*/

/*UI Tabs*/
li.ui-state-default  {
    color: white !important;
}

li.ui-state-active {
    color: black !important;
    background-color: #cfd1d3 !important;
}
/*End UI Tabs*/

/*People info*/
.person-container {
    background-color: #ccc;
    border-radius: 2px;
}


/*Knowledgebase*/
.master-header {
    background-color: #5f636b !important;
}

.logo-header-full {
    background-image: url("https://secure.oregonstate.edu/u_central/images/blackWordmark.gif");
    background-repeat: no-repeat;
    background-color: black;
    margin-bottom: 1em;
    box-shadow: 0px 10px #e55302;
}

#divLogo {
    visibility: hidden;
}

#navContainer a{
    color: black !important;
}

.settings-button {
    color: white;
}

.media-body .gray {
    color: black !important;
}

.media {
    background-color: #cfd1d3;
}

li.themed.tdbar-button-anchored a {
    color: white !important;
}

li.themed.tdbar-button-anchored.active a{
    color: white !important;
}
span.gutter-top-sm.gray.fa.fa-4x.fa-fw {
    color: black !important;
}

.media-body .gutter-bottom-xs a, .feed-entry-actions button, .feed-pager button {
    color: #5394fc  !important;
}

#collapseMe .active a {
    color: white !important;
    background-color: black !important;
}

/*Navbar*/
#td-navbar-collapse li a{
    color: white !important;
}

/*Feed Text*/
.gutter-top-xs {
    color: white !important;
}

/*End Knowledgebase*/

#divServiceLevelAgreement {
    display: none !important;
}

#divCats, textarea {
    color: black;
}

.cke_editable {
    background-color: white !important;
}

#ctl00_ctl00_cpContent_cpContent_divBody p, #ctl00_ctl00_cpContent_cpContent_divBody a, #ctl00_ctl00_cpContent_cpContent_divBody ol, #ctl00_ctl00_cpContent_cpContent_divBody strong, #ctl00_ctl00_cpContent_cpContent_divBody li, #ctl00_ctl00_cpContent_cpContent_divBody td, #ctl00_ctl00_cpContent_cpContent_divBody h1, #ctl00_ctl00_cpContent_cpContent_divBody h2, #ctl00_ctl00_cpContent_cpContent_divBody h3 {
    color: white;
}

.js-icon, .icon-text, .js-selected-icon-display {
    color: #e5e5e5;
}

li.active, li.active a {
    background-color: #5f636b !important;
    color: white !important;
}

/*Lighter link colors on dark background*/
.user-link a, .gutter-bottom-xs a, .nav-tabs a, #divDetails a, .gutter-top-xs a {
    color: #9bc1ff !important;
}

.media-body .gutter-top-xs a, .block-actions a, .media-heading a, #divTasks a {
    color: #116af9 !important;
}

/*Chat menu*/
#ChatMenu {
    background-color: #5f636b !important;
}

#divChat, #lblChatUsersCount {
    color: white;
}

.ChatRow {
    background-color: #ccc !important;
    border-radius: 5px;
}
/*End chat menu*/

/*Groups page*/
#divRight {
    background-color: #5f636b !important;
}

/*Tasks*/
#divTasks a {
    color: #5394fc !important;
}

#divTasks .gutter-top-xs {
    color: white;
}

/*Black text for highlights*/
p span {
    color: black !important;
}

/*IT Reports*/

/*Top Banner*/
.ButtonCellTop {
    background-color: #555 !important;
}


/*Date and time*/
.pull-right.gutter-top-sm {
    color: white;
}

